func TestNewCoderPNG(t *testing.T) {
	type args struct {
		content string
		size    int
	}
	tests := []struct {
		name string
		args args
		want *CoderPNG
	}{
		{
			name: "when the size is within the allowed",
			args: args{
				content: faker.WORD,
				size:    500,
			},
			want: &CoderPNG{
				content: faker.WORD,
				size:    500,
			},
		},
		{
			name: "when the size is larger than allowed",
			args: args{
				content: faker.WORD,
				size:    2049,
			},
			want: &CoderPNG{
				content: faker.WORD,
				size:    MAX_SIZE,
			},
		},
		{
			name: "when the size is smaller than allowed",
			args: args{
				content: faker.WORD,
				size:    1,
			},
			want: &CoderPNG{
				content: faker.WORD,
				size:    MIN_SIZE,
			},
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if got := NewCoderPNG(tt.args.content, tt.args.size); !reflect.DeepEqual(got, tt.want) {
				t.Errorf("NewCoderPNG() = %v, want %v", got, tt.want)
			}
		})
	}
}

func TestCoderPNG_Encode(t *testing.T) {
	type fields struct {
		content string
		size    int
		logo    bool
	}
	tests := []struct {
		name    string
		fields  fields
		wantRaw []byte
		wantErr bool
	}{
		{
			name:    "when genereted qrcode with success",
			fields:  fields{content: "Foo Bar", size: 500},
			wantRaw: pngResult,
			wantErr: false,
		},
		{
			name:    "when has no data to encode",
			fields:  fields{content: "", size: 500},
			wantRaw: nil,
			wantErr: true,
		},
		{
			name:    "when has logo",
			fields:  fields{content: "Foo Bar", size: 500, logo: true},
			wantRaw: pngResultWithLogo,
			wantErr: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			cs := NewCoderPNG(tt.fields.content, tt.fields.size)
			cs.UseLogo = tt.fields.logo

			gotRaw, err := cs.Encode()
			if (err != nil) != tt.wantErr {
				t.Errorf("CoderPNG.Encode() error = %v, wantErr %v", err, tt.wantErr)
				return
			}
			if !reflect.DeepEqual(gotRaw, tt.wantRaw) {
				t.Errorf("CoderPNG.Encode()")
			}
		})
	}
}
